import { Component, OnDestroy, OnInit } from '@angular/core'; 
import { Input, Output, EventEmitter } from '@angular/core';
import { Hero, Heroo } from '../../../moduls/hero.interface'

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent  implements  OnDestroy {

  @Input() heroes:Heroo[];
  @Output() AuxselectHero = new EventEmitter<Hero>();

  selectHero:Heroo={
    id: 0,
    name: '',
    username: '',
    email: '',
    address: {
      street: '',
      suite: '',
      city: '',
      zipcode: '',
      geo: {
        lat: '',
        lng: ''        
      }       
    },
    phone: '',
    website: '',
    company: {
      name: '',
      catchPhrase: '',
      bs: ''      
    }
  };


  onDelete(heroe:Heroo):void { 
    this.selectHero = heroe;  
    this.AuxselectHero.emit(this.selectHero); 
  }

  // onSelect(heroe:Hero):void { 
  //   this.selectHero = heroe;  
  //   this.AuxselectHero.emit({name:'',id:0}); 
  // }

  // showDetail(){
  //   this.AuxselectHero.emit(this.selectHero);      
  // }

  ngOnDestroy() {
    console.log("DESTROY LISTA HIJA") 
  }

}
<ul class="heroes">
    <li *ngFor="let hero of heroes"
    [class.selected]="hero === selectHero"     
    >
        <a href="#ni" routerLink="/heroes/detail/{{hero.id}}">        
            <span class="badge">{{hero.id}}</span> {{hero.name}}
        </a>
        <a href="#ni" (click)="onDelete(hero)">        
